import Check from '@mui/icons-material/Check';

export default function StepperUsage() {
  return (
    <JoyUsageDemo
      componentName="Stepper"
      data={[
        {
          propName: 'stepperOrientation',
          knob: 'radio',
          defaultValue: 'horizontal',
