      <StepIndicator variant="solid" color="primary">
        <Check />
      </StepIndicator>
    }\n  `
      : ''
  }>First</Step>
  ...
</Stepper>`}
      renderDemo={({ stepperOrientation, stepOrientation, size, indicator }) => (
        <Stepper
          orientation={stepperOrientation}
          size={size}
          sx={{ width: 320, mb: 3, py: 3 }}
        >
          <Step
            orientation={stepOrientation}
