        <List>
          <ListItem variant="soft" color="danger">
            <Checkbox
              label="Declined Payment"
              color="danger"
              overlay
              checked={status.declinedPayment}
              onChange={(event) =>
                setStatus({ ...status, declinedPayment: event.target.checked })
              }
              sx={{ color: 'inherit' }}
            />
            <Typography textColor="inherit" sx={{ ml: 'auto' }}>
              8
            </Typography>
          </ListItem>
